import { Swiper, SwiperSlide } from "swiper/vue";
import { ratingGenerator } from '@/HelpersFunction/Helpers'; // Import Swiper styles

import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/pagination";
import { FreeMode, Navigation } from "swiper";
import { useProductStore } from "@/Store/Product";
import { mapState, mapActions } from "pinia";
import axios from "axios";
export default {
  data() {
    return {
      newestProducts: [],
      modules: [FreeMode, Navigation]
    };
  },

  components: {
    Swiper,
    SwiperSlide
  },
  computed: {},

  created() {
    this.getNewestProducts(this.rootDomain);
  },

  mounted() {},

  methods: {
    getNewestProducts(rootDomain) {
      axios.get(rootDomain + 'vue/products/newproducts').then(response => {
        this.newestProducts = response.data.data;
      });
    },

    productDetails(slug) {
      this.$router.push({
        name: "singleProduct",
        params: {
          slug: slug
        }
      });
    },

    getRatings(rating, maxRating = 5) {
      return ratingGenerator(rating, maxRating);
    }

  }
};
