import axios from 'axios';
export default {
  props: ['slug'],

  data() {
    return {
      brandWiseProducts: [],
      brandName: "",
      ShowNotFound: 'Loading...'
    };
  },

  mounted() {
    this.getBrandWiseProduct(this.rootDomain, this.slug);
  },

  methods: {
    getBrandWiseProduct(rootDomain, slug) {
      this.ShowNotFound = 'Loading...';
      axios.get(rootDomain + 'vue/v3/products/brand/' + slug).then(response => {
        this.brandWiseProducts = response.data[0].data;
        this.brandName = response.data[1];

        if (brandWiseProducts.length > 1) {
          this.ShowNotFound = 'Product Not Found...';
        }
      });
    }

  }
};
