import FreelanceService from "../ResourceComponents/ProductComponents/FreelanceService.vue";
import BrandComponent from "./BrandsComponent.vue";
import SellersComponent from "./SellersComponent.vue";
import AllProductVue from '../ResourceComponents/ProductComponents/AllProduct.vue';
import { useCategoryStore } from "@/Store/Categories";
import { mapState, mapActions } from "pinia";
import axios from "axios";
export default {
  data() {
    return {
      sliders: [],
      modules: [Autoplay, Pagination, Navigation],
      categories: [],
      featuredCategories: []
    };
  },

  components: {
    TodaysOffer,
    Swiper,
    SwiperSlide,
    SubCategory,
    BrandComponent,
    SellersComponent,
    NewProduct,
    FeaturedCategory,
    BestSellingProduct,
    AuctionProducts,
    HomeCategoriesProduct,
    FreelanceService,
    AllProductVue
  },
  computed: {// ...mapState(useCategoryStore,['categories']),
    // ...mapState(useCategoryStore,['subcategories']),
    // ...mapState(useCategoryStore,['featuredCategories']),
  },

  created() {
    this.getSlider(this.rootDomain);
    this.getCat(this.rootDomain);
  },

  mounted() {},

  methods: {
    // ...mapActions(useCategoryStore,['getCat']),
    // ...mapActions(useCategoryStore,['getSubcategory']),
    getSlider(rootDomain) {
      axios.get(rootDomain + 'vue/v3/sliders').then(response => {
        this.sliders = response.data.data;
      });
    },

    receiveCategorySlug(slug) {
      this.$router.push({
        name: 'CategoryWiseProduct',
        params: {
          slug: slug
        }
      });
    },

    getCat(rootDomain) {
      axios.get(rootDomain).then(response => {
        this.categories = response.data.categories;
        this.featuredCategories = response.data.featured_categories.data;
      });
    },

    getSubcategory(rootDomain, id, dx) {
      let temp = JSON.parse(JSON.stringify(this.categories)); // alert(('subcategories' in temp[dx]));

      if ('subcategories' in temp[dx] == false) {
        axios.get(rootDomain + "category/nav-element-list", {
          params: {
            id: id
          }
        }).then(response => {
          let subCategorys = response.data.subCategorys;
          temp[dx]['subcategories'] = subCategorys;
          this.categories = temp;
        });
      } else {
        console.log('exist');
      }
    }

  }
};
