  methods: {
    getCategoryWiseProduct(rootDomain, slug) {
      axios.get(rootDomain + 'vue/v3/products/category/' + this.slug).then(response => {
        this.categoryWiseProducts = response.data[0].data;
        this.categoryInfo = response.data[1];
      }).catch(error => {
        console.log(error);
      });
    },

