import axios from 'axios';
export default {
  data() {
    return {
      auth: {
        isAuthenticated: false,
        user: {}
      },
      navCategoriesName: [],
      navCategoriesLinks: [],
      categories: [],
      products: [],
      shops: [],
      keywords: [],
      vendorSystemActivation: 0,
      message: '',
      totalCart: 0,
      totalWishlist: 0
    };
  },

  created() {
    var user = localStorage.getItem("user");

    if (user !== null) {
      user = JSON.parse(user);
      this.auth.isAuthenticated = true;
      this.auth.user = user;
      this.getNavData();
    }

    this.setNavData();
  },

  beforeCreated() {},

  mounted() {
    this.getNavCategories(this.rootDomain);
    this.emitter.on("authentication", message => {
      if (message) {
        let user = JSON.parse(localStorage.getItem("user"));
        this.auth.isAuthenticated = true;
        this.auth.user = user;
        this.getNavData();
      } else {
        setNavData();
      }
    });
  },

  methods: {
    getNavData() {
      axios.get(this.selfDomain + 'vue/v3/auth/get_nav_data', {
        headers: {
          Authorization: "Bearer " + this.auth.user.access_token
        }
      }).then(res => {
        console.log(res);
        this.totalCart = res.data.totalCart;
        this.totalWishlist = res.data.totalWishlist;
        this.setNavData();
      }).catch(err => {});
    },

    setNavData() {
      if (!this.auth.isAuthenticated) {}
    },

    searchSubmit() {
      var searchKey = $('#search').val();
      this.$router.push({
        name: 'productShop',
        params: {
          key_slug: searchKey
        }
      });
    },

    getNavCategories(rootDomain) {
      axios.get(rootDomain + 'vue/header-category').then(res => {
        this.navCategoriesName = res.data.name;
        this.navCategoriesLinks = res.data.links; // console.log(this.navCategoriesName);
        // console.log(this.navCategoriesLinks);
      }).catch(err => {
        console.log(err);
      });
    },

    receiveUrl(url) {
      let urls = {
        "/category/man-woman-baby-fashion": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "man-woman-baby-fashion"
          }
        },
        "/category/health-beauty-personal-care": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "health-beauty-personal-care"
          }
        },
        "/category/electronic-accessories-gadget": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "electronic-accessories-gadget"
          }
        },
        "/category/groceries-lifestyle-medical": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "groceries-lifestyle-medical"
          }
        },
        "/category/digital-products-crouse-etc": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "digital-products-crouse-etc"
          }
        },
        "/category/dollar-crypto-account-vcard": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "dollar-crypto-account-vcard"
          }
        },
        "/category/social-ads-apps-survey": {
          "component": "CategoryWiseProduct",
          "params": {
            slug: "social-ads-apps-survey"
          }
        },
        "/flash-deals": {
          "component": "FlashDeals"
        },
        "/flash-deal/my-offers-ttPpP": {
          "component": "FlashDealDetails",
          "params": {
            slug: "my-offers-ttPpP"
          }
        },
        "/all_coupon": {
          "component": "AllCoupon"
        }
      };
      if (urls[url] != "undefined") this.loadComponent(urls[url]["component"], urls[url]["params"]);
    },

    loadComponent(componentName, params = null) {
      if (params != null) this.$router.push({
        name: componentName,
        params: params
      });else this.$router.push({
        name: componentName
      });
    },

    search(searchBoxFocus = true) {
      var searchKey = $('#search').val();

      if (searchKey.length > 0 && searchBoxFocus) {
        $('body').addClass("typed-search-box-shown");
        $('.typed-search-box').removeClass('d-none');
        $('.search-preloader').removeClass('d-none');
        axios.get(this.rootDomain + 'vue/ajax-search', {
          params: {
            search: searchKey
          }
        }).then(response => {
          let data = response.data;

          if (data == '0') {
            $('#search-content').html(null);
            $('.typed-search-box .search-nothing').removeClass('d-none').html('Sorry, nothing found for <strong>"' + searchKey + '"</strong>');
            $('.search-preloader').addClass('d-none');
          } else {
            this.products = data.products.data;
            this.categories = data.categories.data;
            this.keywords = data.keywords;
            this.shops = data.shops.data;
            this.vendorSystemActivation = data.vendorSystemActivation;
            $('.typed-search-box .search-nothing').addClass('d-none').html(null);
            $('.search-preloader').addClass('d-none');
          }
        });
      } else {
        $('.typed-search-box').addClass('d-none');
        $('body').removeClass("typed-search-box-shown");
      }
    },

    logout() {
      localStorage.removeItem("user");
      this.auth.isAuthenticated = false;
      this.$router.push({
        name: 'home'
      });
      localStorage.removeItem("recentlyViewProduct");
    }

  }
};
