import axios from 'axios';
import { defineStore } from 'pinia';

export const useCategoryStore = defineStore('CategoryStore',{
                    //state
                    state:()=>({
                            categories: [],
                            subcategories:[]
                        }),

                    //actions
                    actions:{
                        getCat(rootDomain){
                                axios.get("http://admindeal.com.bd/api/v2/categories")
                                .then((response)=>{
                                    console.log(response);
                                    this.categories = response.data.sideBarCategory;
                                })
                        },
                        getSubcategory(link){
                                axios.get(link)
                                .then((response)=>{
                                    console.log(response.data.data);
                                    this.subcategories = response.data.data;
                                })
                        }
                    },
});
